from fastapi import FastAPI
from sqlmodel import SQLModel
from app.database import engine
from app.api.v1.endpoints.todos import router as api_v1_router


def create_db_tables():
    """Create all database tables on startup."""
    SQLModel.metadata.create_all(engine)


app = FastAPI(
    title="Todo Application",
    description="A simple todo application API",
    version="1.0.0"
)

app.include_router(api_v1_router, prefix="/api/v1")


@app.on_event("startup")
def on_startup():
    create_db_tables()


@app.get("/")
def root():
    return {"message": "Todo Application API", "status": "running"}


@app.get("/health")
def health_check():
    return {"status": "healthy"}

from typing import List, Optional
from sqlmodel import Session, select

from app.models import Todo


class TodoRepository:
    def __init__(self, session: Session):
        self.session = session

    def create(self, todo: Todo) -> Todo:
        self.session.add(todo)
        self.session.commit()
        self.session.refresh(todo)
        return todo

    def get_all(self) -> List[Todo]:
        statement = select(Todo)
        return list(self.session.exec(statement).all())

    def get_by_id(self, todo_id: int) -> Optional[Todo]:
        statement = select(Todo).where(Todo.id == todo_id)
        return self.session.exec(statement).first()

    def update(self, existing_todo: Todo, todo_data: dict) -> Todo:
        for key, value in todo_data.items():
            if hasattr(existing_todo, key):
                setattr(existing_todo, key, value)

        self.session.add(existing_todo)
        self.session.commit()
        self.session.refresh(existing_todo)
        return existing_todo

    def delete(self, todo: Todo) -> bool:
        self.session.delete(todo)
        self.session.commit()
        return True

from typing import List, Optional
from sqlmodel import Session

from app.models import Todo
from app.repositories.todo_repository import TodoRepository
from app.schemas import TodoCreate, TodoUpdate


class TodoService:
    def __init__(self, session: Session):
        self.repository = TodoRepository(session)

    def create_todo(self, todo_data: TodoCreate) -> Todo:
        """Create a new todo item with business logic validation."""
        todo = Todo(
            title=todo_data.title.strip(),
            description=todo_data.description,
            completed=todo_data.completed if todo_data.completed is not None else False
        )

        return self.repository.create(todo)

    def get_all_todos(self) -> List[Todo]:
        """Retrieve all todo items."""
        return self.repository.get_all()

    def get_todo_by_id(self, todo_id: int) -> Optional[Todo]:
        """Retrieve a specific todo item by ID."""
        return self.repository.get_by_id(todo_id)

    def update_todo(self, todo_id: int, todo_data: TodoUpdate) -> Optional[Todo]:
        """Update an existing todo item."""
        existing_todo = self.repository.get_by_id(todo_id)
        if not existing_todo:
            return None

        update_data = todo_data.model_dump(exclude_unset=True)

        if "title" in update_data and update_data["title"]:
            update_data["title"] = update_data["title"].strip()

        for key, value in update_data.items():
            setattr(existing_todo, key, value)

        return self.repository.update(existing_todo, update_data)

    def delete_todo(self, todo_id: int) -> bool:
        """Delete a todo item by ID."""
        existing_todo = self.repository.get_by_id(todo_id)
        if not existing_todo:
            return False

        return self.repository.delete(existing_todo)

from fastapi import APIRouter, Depends, HTTPException, status
from typing import List
from sqlmodel import Session

from app.database import get_session
from app.schemas import TodoCreate, TodoUpdate, TodoResponse
from app.services.todo_service import TodoService

router = APIRouter(prefix="/todos", tags=["todos"])


def get_todo_service(session: Session = Depends(get_session)) -> TodoService:
    return TodoService(session)


@router.post("/", response_model=TodoResponse, status_code=status.HTTP_201_CREATED)
def create_todo(
    todo_data: TodoCreate,
    service: TodoService = Depends(get_todo_service)
):
    """Create a new todo item."""
    todo = service.create_todo(todo_data)
    return todo


@router.get("/", response_model=List[TodoResponse])
def list_todos(
    service: TodoService = Depends(get_todo_service)
):
    """Retrieve a list of all todo items."""
    todos = service.get_all_todos()
    return todos


@router.get("/{todo_id}", response_model=TodoResponse)
def get_todo(
    todo_id: int,
    service: TodoService = Depends(get_todo_service)
):
    """Retrieve a specific todo item by ID."""
    todo = service.get_todo_by_id(todo_id)
    if not todo:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail=f"Todo with id {todo_id} not found"
        )
    return todo


@router.put("/{todo_id}", response_model=TodoResponse)
def update_todo(
    todo_id: int,
    todo_data: TodoUpdate,
    service: TodoService = Depends(get_todo_service)
):
    """Update an existing todo item by ID."""
    todo = service.update_todo(todo_id, todo_data)
    if not todo:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail=f"Todo with id {todo_id} not found"
        )
    return todo


@router.delete("/{todo_id}", status_code=status.HTTP_204_NO_CONTENT)
def delete_todo(
    todo_id: int,
    service: TodoService = Depends(get_todo_service)
):
    """Delete a todo item by ID."""
    deleted = service.delete_todo(todo_id)
    if not deleted:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail=f"Todo with id {todo_id} not found"
        )
    return None
